using UnityEngine;public class EnemyDamager_SebastianMol : M{public float m_baseDamage;internal float m_damage;public float m_percentageChanceOfAffect = 0.5f;public float m_poisionDamage = 1;public float m_slowDebuff = 0.5f;public float m_affectTime = 5;public HitEffectElliott m_HitEffectElliott;private void OnTriggerEnter2D(Collider2D collision){if (collision.tag == "Player"){FOT<PlayerHealthHunger_MarioFernandes>().Hit(m_damage);MeleeEnemy_SebastianMol currentEnemyScript = GetComponentInParent<MeleeEnemy_SebastianMol>();collision.GetComponent<HitEffectElliott>().StartHitEffect(false);if (currentEnemyScript.m_currentEnemyType == m_enemyType.SPACENINJABOSS){if(currentEnemyScript.m_doseAffect){float rand = Random.Range(0, 2);if (rand > m_percentageChanceOfAffect){FOT<EffectManager_MarioFernandes>().AddEffect(new PoisionDefuff_MarioFernandes(m_poisionDamage, m_affectTime));}else {FOT<EffectManager_MarioFernandes>().AddEffect(new SpeedEffect_MarioFernandes(m_affectTime, m_slowDebuff));}}}gameObject.SetActive(false);}}private void Start(){m_HitEffectElliott = GetComponent<HitEffectElliott>();}}