using System.Collections;using UnityEngine;public class CameraShakeElliott : M{public float m_duration;public float m_magnitude;public float m_cameraLerp;public IEnumerator Shake(float duration, float magnitude){duration = m_duration;magnitude = m_magnitude;Vector3 orgignalPos = transform.localPosition;float elapsed = 0.0f;while (elapsed < m_duration){float x = Random.Range(-1f, 1f) * m_magnitude;float y = Random.Range(-1f, 1f) * m_magnitude;transform.localPosition = new Vector3(x, y, orgignalPos.z);elapsed += Time.deltaTime;yield return null;}transform.localPosition = orgignalPos;}public void StartShake(){SC(Shake(m_duration, m_magnitude));}}