using UnityEngine;public class PressurePad_JoaoBeijinho : M{public Collider2D m_doorCollider;private string m_playerTag = "Player";private string m_crateTag = "Crate";private string m_meleeWeaponTag = "MeleeWeapon";private void OnTriggerEnter2D(Collider2D collision){if (collision.tag == m_playerTag || collision.tag == m_crateTag || collision.tag == m_meleeWeaponTag){m_doorCollider.GetComponent<Collider2D>().enabled = false;}}}