using System.Collections.Generic;using UnityEngine;public class FreezerTrigger_JoaoBeijinho : M{public List<Collider2D> m_enemyList = new List<Collider2D>();private void OnTriggerEnter2D(Collider2D collision){if (collision is BoxCollider2D && collision.gameObject.CompareTag(Tags_JoaoBeijinho.m_enemyTag)){if (!m_enemyList.Contains(collision)){m_enemyList.Add(collision);}}}private void OnTriggerExit2D(Collider2D collision){if (collision is BoxCollider2D && collision.gameObject.CompareTag(Tags_JoaoBeijinho.m_enemyTag)){if (m_enemyList.Contains(collision)){m_enemyList.Remove(collision);}}}}