using UnityEngine;using UnityEngine.UI;using UnityEngine.InputSystem;public class MainMenu_Manager_Elliott : M{public Button m_continueButton;public GameObject m_settingMenu;protected bool m_openSettings;private int m_lastCheckpointLevel;public void Resume(){if (m_lastCheckpointLevel > 0){SceneManager_JamieG.Instance.LoadLevel(m_lastCheckpointLevel);}}public void StartNewGame(){SaveLoadSystem_JamieG.DeleteSaves();SceneManager_JamieG.Instance.LoadNextLevel();}public void OpenSettingsMenu(){m_settingMenu.SetActive(true);}public void Quit(){Application.Quit();}void Start(){m_openSettings = false;m_lastCheckpointLevel = SaveLoadSystem_JamieG.LoadCheckpoint().m_lastCheckpointLevel;m_continueButton.enabled = m_lastCheckpointLevel > 0;}void Update(){var gamepad = Keyboard.current;if (gamepad == null)return;if (gamepad.sKey.wasPressedThisFrame){OpenSettingsMenu();}}}