using UnityEngine;using UnityEngine.InputSystem;public class PauseMenuManager_ElliottDesouza : M{public GameObject m_player;public GameObject m_settingMenu;protected bool m_openSettings;public void Resume(){gameObject.SetActive(false);Time.timeScale = 1f;}public void ReturnToCheckpoint(){SceneManager_JamieG.Instance.ResetToCheckpoint();Time.timeScale = 1f;}public void ResetScene(){SceneManager_JamieG.Instance.ResetLevel();Time.timeScale = 1f;}public void OpenSettingsMenu(){m_settingMenu.SetActive(true);}public void ExitToMainMenu(){SceneManager_JamieG.Instance.SaveGameState();SceneManager_JamieG.Instance.LoadMainMenu();Time.timeScale = 1f;}void Start(){m_openSettings = false;}void Update(){var gamepad = Keyboard.current;if (gamepad == null)return;if (gamepad.escapeKey.wasPressedThisFrame){Resume();}if (gamepad.rKey.wasPressedThisFrame){ReturnToCheckpoint();}if (gamepad.sKey.wasPressedThisFrame){OpenSettingsMenu();}}}