using UnityEngine;using UnityEngine.InputSystem;using UnityEngine.UI;public class SettingsMenu_ElliottDesouza : M{public Button m_englishButton;public Button m_portugueseButton;public Slider m_SFXSlider;public Slider m_musicSlider;public string m_selectedLanguage;private bool m_isEnglish;private void Start(){SettingsData settings = SaveLoadSystem_JamieG.LoadSettings();m_musicSlider.value = settings.m_musicVolume;m_SFXSlider.value = settings.m_sfxVolume;m_selectedLanguage = settings.m_chosenLanguage;m_englishButton.interactable = !m_selectedLanguage.Equals("English");m_portugueseButton.interactable = m_selectedLanguage.Equals("English");}public void OnEnglishSelected(){m_selectedLanguage = "English";LanguageResolver_Jann.Instance.RefreshTranslation(m_selectedLanguage);}public void OnPortugueseSelected(){m_selectedLanguage = "Portuguese";LanguageResolver_Jann.Instance.RefreshTranslation(m_selectedLanguage);}public void IncreaseVolume(){m_musicSlider.value = m_musicSlider.value + 0.1f;}public void DecreaseVolume(){m_musicSlider.value = m_musicSlider.value - 0.1f;}public void IncreaseSFX(){m_SFXSlider.value = m_SFXSlider.value + 0.1f;}public void DecreaseSFX(){m_SFXSlider.value = m_SFXSlider.value - 0.1f;}public void ExitSettingMenu(){SaveLoadSystem_JamieG.SaveSettings(this);GameObject dialogManager = F("DialogManager");if (dialogManager != null){dialogManager.GetComponent<DialogueManager_MarioFernandes>().LoadLanguageFile();}gameObject.SetActive(false);}void Update(){var gamepad = Keyboard.current;if (gamepad == null)return;if (gamepad.rKey.wasPressedThisFrame){IncreaseVolume();}}}