using UnityEngine;public class PlayerStealth_JoaoBeijinho : M{public bool m_stealthed = false;public bool m_crouched = false;public bool m_inVent = false;private PlayerAnimation_LouieWilliamson m_pAnimation;public float crouchSpeed;public bool IsStealthed(){return m_stealthed;}public bool IsCrouched(){return m_crouched;}public bool IsinVent(){return m_inVent;}public void Crouch(){PlayerController_JamieG playerController = GetComponent<PlayerController_JamieG>();m_crouched = !m_crouched;if (m_crouched == true){gameObject.GetComponent<PlayerMovement_MarioFernandes>().m_speed = crouchSpeed;playerController.m_attackTap.Disable();playerController.m_attackSlowTap.Disable();playerController.m_roll.Disable();playerController.m_eat.Disable();m_pAnimation.Crouching(true);}else {gameObject.GetComponent<PlayerMovement_MarioFernandes>().ResetSpeed();playerController.m_attackTap.Enable();playerController.m_attackSlowTap.Enable();playerController.m_roll.Enable();playerController.m_eat.Enable();m_pAnimation.Crouching(false);}}private void Start(){m_pAnimation = GetComponentInChildren<PlayerAnimation_LouieWilliamson>();}}