using System.Collections;using UnityEngine;public class PlayerDeathEffect_Elliott : M{public SpriteRenderer m_player;private PlayerController_JamieG m_PauseInput;public IEnumerator DeathBlink(){for (int i = 0; i < 5; i++){m_PauseInput.OnDisable();m_player.enabled = false;yield return new WaitForSeconds(.1f);m_player.enabled = true;yield return new WaitForSeconds(.1f);}m_PauseInput.OnEnable();}public void SpriteFlash(){SC(DeathBlink());}void Start(){m_player = GetComponentInChildren<SpriteRenderer>();m_PauseInput = GetComponent<PlayerController_JamieG>();}}