using UnityEngine;public abstract class StatusEffect_MarioFernandes{public bool m_isEnable;protected GameObject m_target = null;protected float m_duration = 0;protected float m_speedMultiplier = 1;protected virtual void Effect(){}public virtual void Activate(GameObject target){m_isEnable = true;m_target = target;}public virtual void DeactivateEffect(){m_isEnable = false;}public void Update(){if(m_duration-- > 0)Effect();else DeactivateEffect();}}public class PoisionDefuff_MarioFernandes : StatusEffect_MarioFernandes{protected float m_damagePerTick = 0;public PoisionDefuff_MarioFernandes(float duration, float damagePerTick){m_duration = duration;m_damagePerTick = damagePerTick;}protected override void Effect(){m_target.GetComponent<PlayerHealthHunger_MarioFernandes>().Hit(m_damagePerTick);}}public class HealBuff_MarioFernandes : StatusEffect_MarioFernandes{protected float m_healthIncrease = 0;public HealBuff_MarioFernandes(float duration, float healthIncrease){m_healthIncrease = healthIncrease;m_duration = duration;}public override void Activate(GameObject target){m_isEnable = true;m_target = target;m_target.GetComponent<PlayerHealthHunger_MarioFernandes>().Heal(m_healthIncrease);}}public class SpeedEffect_MarioFernandes : StatusEffect_MarioFernandes{protected float m_healthIncrease = 0;public SpeedEffect_MarioFernandes(float duration, float speedMultiplier = 1){m_duration = duration;m_speedMultiplier = speedMultiplier;}public override void Activate(GameObject target){m_isEnable = true;m_target = target;m_target.GetComponent<PlayerMovement_MarioFernandes>().m_speed =  (m_target.GetComponent<PlayerMovement_MarioFernandes>().m_baseSpeed * m_speedMultiplier);}public override void DeactivateEffect(){m_target.GetComponent<PlayerMovement_MarioFernandes>().ResetSpeed();m_isEnable = false;}}public class StrengthEffect_MarioFernandes : StatusEffect_MarioFernandes{protected float m_strengthModifier = 0;public StrengthEffect_MarioFernandes(float duration, float strengthModifier = 1){m_duration = duration;m_strengthModifier = strengthModifier;}public override void Activate(GameObject target){base.Activate(target);m_target.GetComponent<PlayerCombat_MarioFernandes>().m_strenght = m_strengthModifier;}public override void DeactivateEffect(){m_target.GetComponent<PlayerCombat_MarioFernandes>().ResetStrength();base.DeactivateEffect();}}