using System.Collections;using UnityEngine;public class Phone_JoaoBeijinho : StealthObject_JoaoBeijinho{protected ClueSystem_JoaoBeijinho m_clueSystemScript;protected DialogueManager_MarioFernandes m_dialogueManagerScript;private bool m_onPhone = false;private Dialogue m_dialogue;private void OnTriggerEnter2D(Collider2D collision){if (collision.gameObject.CompareTag("Player")){m_onPhone = true;SC(PhoneDuration());}}private void OnTriggerExit2D(Collider2D collision){if (collision.gameObject.CompareTag("Player")){m_onPhone = false;}}IEnumerator PhoneDuration(){Hide();m_playerControllerScript.m_movement.Disable();yield return new WaitForSeconds(5);Hide();m_playerControllerScript.m_movement.Enable();}void Start(){m_clueSystemScript = gameObject.GetComponent<ClueSystem_JoaoBeijinho>();m_dialogueManagerScript = FOT<DialogueManager_MarioFernandes>();}void Update(){if (m_playerControllerScript.m_interact.triggered && m_onPhone == true){m_clueSystemScript.GetCurrentClue();m_dialogue.m_name = "Phone";m_dialogue.m_sentences = m_clueSystemScript.m_currentClue;m_dialogueManagerScript.StartDialogue(m_dialogue);}}}