using UnityEngine;public class Vent_JoaoBeijinho : StealthObject_JoaoBeijinho{private GameObject m_player;private void OnTriggerEnter2D(Collider2D collision){if (collision.gameObject.CompareTag(Tags_JoaoBeijinho.m_playerTag)){if (!m_playerStealthScript.m_stealthed){VentEnter();}}}private void OnTriggerExit2D(Collider2D collision){if (collision.gameObject.CompareTag(Tags_JoaoBeijinho.m_playerTag)){if (m_playerStealthScript.m_stealthed){VentExit();}}}private void VentEnter(){Hide();ToggleVent();m_player.GetComponentInChildren<SpriteRenderer>().enabled = false;Physics2D.IgnoreLayerCollision(0, 10, true);}private void VentExit(){Hide();ToggleVent();m_player.GetComponentInChildren<SpriteRenderer>().enabled = true;gameObject.SetActive(false);Physics2D.IgnoreLayerCollision(0, 10, false);}void Start(){m_player = F("Player");gameObject.SetActive(false);}}