using UnityEngine;using UnityEngine.SceneManagement;public class UpgradedEffects_MarioFernandes : M{int m_currentScenne = 0;public float m_sakeStrengthModifier = 2;public float m_TempuraStrengthModifier = 1.5f;public float m_TempuraSpeedhModifier = 0.5f;public GameObject m_Dango = null;public GameObject m_Noodle = null;private WeaponUI_LouieWilliamson wpnUI;public void SakeEffect(){if(GetComponent<Inventory_JoaoBeijinho>().HasItem(ItemType.Sake, 1)){GetComponent<EffectManager_MarioFernandes>().AddEffect(new StrengthEffect_MarioFernandes(30, m_sakeStrengthModifier));GetComponent<Inventory_JoaoBeijinho>().RemoveItem(ItemType.Sake, 1);wpnUI.setPickupImage(FoodType.SAKE);wpnUI.setPickupAnim(false);}}public void TempuraEffect(){if(GetComponent<Inventory_JoaoBeijinho>().HasItem(ItemType.Tempura, 1)){GetComponent<EffectManager_MarioFernandes>().AddEffect(new StrengthEffect_MarioFernandes(30, m_TempuraStrengthModifier));GetComponent<EffectManager_MarioFernandes>().AddEffect(new SpeedEffect_MarioFernandes(30, m_TempuraSpeedhModifier));GetComponent<Inventory_JoaoBeijinho>().RemoveItem(ItemType.Tempura, 1);wpnUI.setPickupImage(FoodType.TEMPURA);wpnUI.setPickupAnim(false);}}public void DangoEffect(){if(GetComponent<Inventory_JoaoBeijinho>().HasItem(ItemType.Dango, 1)){GameObject[] changeWeapon = GameObject.FindGameObjectsWithTag("PlayerWeapon");foreach (var item in changeWeapon){if(item.GetComponent<WeaponsTemplate_MarioFernandes>().m_foodType == FoodType.RICEBALL){Instantiate(m_Dango,item.transform.position, item.transform.rotation);item.SetActive(false);}}}}public void NoodlesEffect(){if(GetComponent<Inventory_JoaoBeijinho>().HasItem(ItemType.Noodles, 1)){GameObject[] changeWeapon = GameObject.FindGameObjectsWithTag("PlayerWeapon");foreach (var item in changeWeapon){if(item.GetComponent<WeaponsTemplate_MarioFernandes>().m_foodType == FoodType.SQUID){Instantiate(m_Noodle,item.transform.position, item.transform.rotation);item.SetActive(false);}}}}void Start(){wpnUI = F("WeaponsUI").GetComponent<WeaponUI_LouieWilliamson>();}void Update(){if(m_currentScenne < SceneManager.GetActiveScene().buildIndex){DangoEffect();NoodlesEffect();m_currentScenne = SceneManager.GetActiveScene().buildIndex;}}}