using UnityEngine;public class Projectile_MarioFernandes : M{public int m_dmg = 0;public float m_speed = 0;public float m_distractTime =0;float m_timeAlive = 3;public Vector3 m_direction;private PlayerController_JamieG Controller;void Start(){transform.rotation = Quaternion.Euler(new Vector3(m_direction.x, m_direction.y, 0));}void FixedUpdate(){transform.position += m_direction * m_speed * Time.deltaTime ;}void Update() {if(m_timeAlive <= 0)D(gameObject);else m_timeAlive -= Time.deltaTime;}private void OnTriggerEnter2D(Collider2D other) {if(other.tag == Tags_JoaoBeijinho.m_enemyTag){other.GetComponentInParent<BaseEnemy_SebastianMol>().TakeDamage( m_damageType.RANGE ,m_dmg);if(m_distractTime >0){other.GetComponentInParent<BaseEnemy_SebastianMol>().StunEnemyWithDeleyFunc(m_distractTime);}D(gameObject);}else if(other.isTrigger && other.GetComponent<Renderer>() && other.GetComponent<Renderer>().sortingLayerName == "Walls2"){D(gameObject);}}}